import { useParams } from "react-router-dom"
import { useState,useEffect } from "react"
import Backup from '../assets/images/backup.png'
export const MovieDetail = () => {
  const params=useParams();
  const [movie,setMovie]=useState({});
  const image=movie.poster_path ? `https://image.tmdb.org/t/p/w500/${movie.poster_path}` : Backup;


  useEffect(()=>{
    async function fetchMovie(){
      const response=await fetch(`https://api.themoviedb.org/3/movie/${params.id}?api_key=e84e7fd9194d6a4997c7bc1101e356e9`);
      const json=await response.json();
      setMovie(json);
      console.log(json);
    }
    fetchMovie()
  },[])

  return (
    <main>
      <section className="flex justify-around flex-wrap py-5">
        <div className="mx-w-sm">
          <img className="rounded" src={image} alt={movie.title}/>
  
        </div>
        <div className="mx-w-2xl text-gray-700 text-lg dark:text-white">
          <h1 className="text-4xl font-bold my-3">{movie.title}</h1>
          <p className="my-4">{movie.overview}</p>


        </div>
      </section>
    </main>
  )
}
